CKEDITOR.dialog.add("figure",function(t){return{title:"Edit Figure",minWidth:200,minHeight:100,contents:[{id:"info",elements:[{id:"align",type:"select",label:"Align",items:[["left","lt"],["right","rt"],["center","ct"]],setup:function(t){"xlrg"!==t.data.width&&this.setValue(t.data.align)},commit:function(t){"xlrg"!==t.data.width?t.setData("align",this.getValue()):t.setData("align",null)}},{id:"width",type:"select",label:"Width",items:[["small","sm"],["medium","med"],["large","lrg"],["extra-large","xlrg"]],setup:function(t){this.setValue(t.data.width)},commit:function(t){if(t.setData("width",this.getValue()),"xlrg"!==this.getValue()){var e=this.getDialog().getContentElement("info","align").getValue();""===e?t.setData("align","rt"):t.setData("align",e)}}},{id:"credit",type:"checkbox",label:"Credit",setup:function(t){this.setValue(t.data.hasCredit)},commit:function(t){t.setData("hasCredit",this.getValue())}},{id:"caption",type:"checkbox",label:"Caption",setup:function(t){this.setValue(t.data.hasCaption)},commit:function(t){t.setData("hasCaption",this.getValue())}}]}]}});